//import * as React from 'react';
//import Button from '@mui/material/Button';


/** 
function App() {
  return (
    <div>
      <Button variant="contained">Hello World</Button>
    </div>
  );
}

export default App;
*/

import React, { useState, useEffect } from "react";

function Slider({ id, value, onChange }) {
  const handleChange = (event) => {
    onChange(event.target.value);
  };

  return (
    <fieldset disabled>
    <div style={{ height: "200px", display: "flex", flexDirection: "column" }}>
      <input
        type="range"
        min="0"
        max="180"
        value={value}
        onChange={handleChange}
        style={{ height: "100%", appearance: "slider-vertical", background: "auto", outline: "none" }}
      />
      <div style={{ alignSelf: "center" }}>{`Slider ${id}: ${value}`}</div>
    </div>
    </fieldset>
  );
}


function App() {
  const [slider1Value, setSlider1Value] = useState(0);
  const [slider2Value, setSlider2Value] = useState(0);
  const [slider3Value, setSlider3Value] = useState(0);
  const [slider4Value, setSlider4Value] = useState(0);

  const [pressedKeys, setPressedKeys] = useState({});

  useEffect(() => {
    const handleKeyDown = (event) => {
      setPressedKeys((prevState) => ({
        ...prevState,
        [event.key]: true,
      }));
    };
    const handleKeyUp = (event) => {
      setPressedKeys((prevState) => ({
        ...prevState,
        [event.key]: false,
      }));
    };
    window.addEventListener("keydown", handleKeyDown);
    window.addEventListener("keyup", handleKeyUp);
    return () => {
      window.removeEventListener("keydown", handleKeyDown);
      window.removeEventListener("keyup", handleKeyUp);
    };
  }, []);

  useEffect(() => {
    const incrementSlider1 = () => {
      setSlider1Value((prevValue) => Math.min(prevValue + 1, 180));
    };
    const decrementSlider1 = () => {
      setSlider1Value((prevValue) => Math.max(prevValue - 1, 0));
    };

    const incrementSlider2 = () => {
      setSlider2Value((prevValue) => Math.min(prevValue + 1, 180));
    };
    const decrementSlider2 = () => {
      setSlider2Value((prevValue) => Math.max(prevValue - 1, 0));
    };

    const incrementSlider3 = () => {
      setSlider3Value((prevValue) => Math.min(prevValue + 1, 180));
    };
    const decrementSlider3 = () => {
      setSlider3Value((prevValue) => Math.max(prevValue - 1, 0));
    };

    const incrementSlider4 = () => {
      setSlider4Value((prevValue) => Math.min(prevValue + 1, 180));
    };
    const decrementSlider4 = () => {
      setSlider4Value((prevValue) => Math.max(prevValue - 1, 0));
    };



    const intervalId = setInterval(() => {
      if (pressedKeys.Q) {
        incrementSlider1();
      } if (pressedKeys.A) {
        decrementSlider1();
      }

      if (pressedKeys.W) {
        incrementSlider2();
      }  if (pressedKeys.S) {
        decrementSlider2();
      }

      if (pressedKeys.E) {
        incrementSlider3();
      }  if (pressedKeys.D) {
        decrementSlider3();
      }

      if (pressedKeys.R) {
        incrementSlider4();
      }  if (pressedKeys.F) {
        decrementSlider4();
      }
    }, 25); //this was 50 ms, I changed to 25

    return () => {
      clearInterval(intervalId);
    };
  }, [pressedKeys]);

  return (
    <div style={{ display: "flex" }}>
      <Slider id="1" value={slider1Value} onChange={setSlider1Value} />
      <Slider id="2" value={slider2Value} onChange={setSlider2Value} />
      <Slider id="3" value={slider3Value} onChange={setSlider3Value} />
      <Slider id="4" value={slider4Value} onChange={setSlider4Value} />
    </div>
  );
}

export default App;
